import React, { Component } from 'react';
import SearchedUsersDisplay from './SearchedUsersDisplay';

class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUserDetails : {}
    }
  }

  handleCurrentUSer = (e, login) => {
    fetch(`https://api.github.com/users/${login}`).
    then(res => res.json()).
    then(data => this.setState({
      currentUserDetails : data
    }))
  }

  render() {
    const {userSearched, Error} = this.props;
    return (
      <main>
        <SearchedUsersDisplay userSearched={userSearched} Error={Error} currentUser={this.handleCurrentUSer}/>
      </main>
    );
  }
}

export default Main;
